uniform sampler2D uInputTexture; // {"skip" : true}
uniform float uTime; // {"alias" : "time"}

in vec2 TexCoord0;
out vec4 oFragColor;

// Shader downloaded and adapted from glslsandbox.com
// URL: http://glslsandbox.com/e#45513.0

#ifdef GL_ES
precision highp float;
#endif


uniform vec2 mouse;
vec2 resolution;


vec3 rotatex(in vec3 p, float ang) {	
	return vec3(p.x, p.y*cos(ang) - p.z*sin(ang), p.y*sin(ang) + p.z*cos(ang)); 
}
vec3 rotatey(in vec3 p, float ang) {	
	return vec3(p.x*cos(ang) - p.z*sin(ang), p.y, p.x*sin(ang) + p.z*cos(ang)); 
}
vec3 rotatez(in vec3 p, float ang) {	
	return vec3(p.x*cos(ang) - p.y*sin(ang), p.x*sin(ang) + p.y*cos(ang), p.z); 
}
float scene(vec3 p)
{
	p = rotatey(p, 0.3*uTime);
	p = rotatex(p, 0.2*uTime);
	p = rotatez(p, 0.4*uTime);
	float d0 = length(max(abs(p) - 0.5, 0.0))- 0.01 + clamp(sin((p.x+p.y+p.z)*20.0)*0.03, 0.0, 1.0); 
	float d1 = length(p) - 0.5; 
	return mix(d0,d1, sin(uTime));
//	return sin(min(d0,d1)*1.6); 
//	return length(p) - 1.0; 
}

vec3 get_normal(vec3 p)
{
	vec3 eps = vec3(0.01, 0.0, 0.0); 
	float nx = scene(p + eps.xyy) - scene(p - eps.xyy); 
	float ny = scene(p + eps.yxy) - scene(p - eps.yxy); 
	float nz = scene(p + eps.yyx) - scene(p - eps.yyx); 
	return normalize(vec3(nx,ny,nz)); 
}


void main() {
    resolution = textureSize(uInputTexture, 0).xy;


	vec3 color = vec3(0); 
	vec2 p = 2.0*(vec2(TexCoord0.x, 1.0 - TexCoord0.y) * resolution).xy/resolution - 1.0; 
	p.x *= resolution.x/resolution.y; 

	if (abs(p.y) < 0.7) {
	vec3 ro = vec3(0.0, 0.0, 2.0); 
	vec3 rd = normalize(vec3(p.x, p.y, -1.4)); 

	color = (1.0 - vec3(length(p*0.5)))*0.2;   

	vec3 pos = ro; 
	float dist = 0.0; 
	for (int i = 0; i < 64; i++) {
		float d = scene(pos); 
		pos += rd*d; 
		dist += d; 
	}

	if (dist < 100.0) {
		vec3 n = get_normal(pos);
		vec3 r = reflect(normalize(pos - ro),n); 
		vec3 h = -normalize(n + pos - ro ); 
		float diff  = 1.0*clamp(dot(n, normalize(vec3(1,1,1))), 0.0, 1.0); 
		float diff2 = 0.2*clamp(dot(n, normalize(vec3(0.7,-1,0.5))), 0.0, 1.0); 
		float diff3 = 0.1*clamp(dot(n, normalize(vec3(-0.7,-0.4,0.7))), 0.0, 1.0); 
		//float spec = pow(clamp(dot(r, normalize(vec3(1,1,1))), 0.0, 1.0), 50.0); 
		float spec = pow(clamp(dot(h, normalize(vec3(1,1,1))), 0.0, 1.0), 50.0); 
		float amb = 0.2 + pos.y; 
		color = diff*vec3(1,1,1) + diff2*vec3(1,0,0)  + diff3*vec3(1,0,1) + spec*vec3(1,1,1)  + amb*vec3(0.2,0.2,0.2); 
		color /= dist;
	}
	}

	oFragColor = vec4(color, 1.0); 
}

uniform sampler2D uInputTexture; // {"skip" : true}
uniform float uTime; // {"alias" : "time"}

in vec2 TexCoord0;
out vec4 oFragColor;

// Shader downloaded and adapted from glslsandbox.com
// URL: http://glslsandbox.com/e#49147.0

#ifdef GL_ES
precision highp float;
#endif

#extension GL_OES_standard_derivatives : disable


uniform vec2 mouse;
vec2 resolution;


// Ziad 12 / 09 / 2018
// Leon 05 / 07 / 2017
// using lines of code of IQ, Mercury, LJ, Koltes, Duke

#define PI 3.14159
#define TAU PI*2.

#define DITHER
#define STEPS 30.
#define BIAS 0.00001
#define DIST_MIN 0.01

mat2 rot (float a) { float c=cos(a),s=sin(a);return mat2(c,-s,s,c); }
float sphere (vec3 p, float r) { return length(p)-r; }
float cyl (vec2 p, float r) { return length(p)-r; }
float torus( vec3 p, vec2 s ) {
  vec2 q = vec2(length(p.xz)-s.x,p.y);
  return length(q)-s.y;
}
float smin (float a, float b, float r) {
    float h = clamp(.5+.5*(b-a)/r,0.,1.);
    return mix(b,a,h)-r*h*(1.-h);
}
float rand(vec2 co) { return fract(sin(dot(co*0.123,vec2(12.9898,78.233))) * 43758.5453); }
vec3 camera (vec3 p) {
    p.yz *= rot((PI*(mouse.y/resolution.y-.5)*step(0.5,mouse.x)));
    p.xz *= rot((PI*(mouse.x/resolution.x-.5)*step(0.5,mouse.x)));
    p.yz *= rot(-PI*.5);
    return p;
}

float opDisplace( vec3 p )
{
    float d1 = torus(p,vec2(.4,.3));
    float d2 = cos(2.*p.x)*sin(2.*p.y)*sin(2.*p.z);
    return d1+d2;
}

float opTwist( vec3 p )
{
    float c = cos(0.0*p.y);
    float s = sin(1.0*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xz,p.y);
    return opDisplace(p);
}

float map (vec3 p) {
    vec3 p1 = p;
    float geo = 1.;
    float cy = 0.4;
    const float repeat = 2.;
    p1.xy *= rot(length(p)*.5);


    float t = uTime*0.3;

    for (float i = 0.; i < repeat; ++i) {
        p1.yz *= rot(0.3+t*0.5);
        p1.xy *= rot(0.2+t);
        p1.xz *= rot(.15+t*2.);

       	p1.xy *= rot(p.x*.5+t);

        // gyroscope
      //  geo = smin(geo, (torus(p1,vec2(1.+i*.2,.12))), .3);


        // tentacles cylinders
        geo = smin(geo, (opTwist(p1)), .6);

        // torus along the cylinders
        vec3 p2 = p1;
        p2.y = mod(p2.y,cy)-cy/2.;
        geo = smin(geo, (opTwist(p2)), .1);
    }
    return geo;
}





void main() {
    resolution = textureSize(uInputTexture, 0).xy;




	vec2 uv = ((vec2(TexCoord0.x, 1.0 - TexCoord0.y) * resolution).xy-.5*resolution.xy)/resolution.y;
    vec3 eye = camera(vec3(uv,-2));
    vec3 ray = camera(normalize(vec3(uv,.75)));
    vec3 pos = eye;
    float shade = 0.;
    #ifdef DITHER
	vec2 dpos = ( (vec2(TexCoord0.x, 1.0 - TexCoord0.y) * resolution).xy / resolution.xy );
	vec2 seed = dpos + fract(uTime);
	#endif 
    for (float i = 0.; i < STEPS; ++i) {
		float dist = map(pos);
        if (dist < BIAS) {
            shade += 0.9;
        }
        #ifdef DITHER
        dist=abs(dist)*(0.1+0.2*rand(seed*vec2(i)));
        #endif 
        dist = max(DIST_MIN,dist);
        pos += ray*dist;
    }
	oFragColor =vec4(1.);
	oFragColor.rgb = vec3(shade/(STEPS-1.),shade*0.9/(STEPS-1.),shade*0.9/(STEPS-1.));

}
